INSERT INTO `games` VALUES (21,'Kiếm Thế','kiem-the','10-2017/img (1)-2213881770486076.jpg',1,NULL,'Zing',NULL,NULL,NULL,NULL,'2020-02-02 03:04:00','Game Kiếm Thế l&agrave; Game Online','deleted',NULL,'http://kiemthe.zing.vn/','http://kiemthe.zing.vn/cam-nang/cai-dat-ngay/cai-dat-ngay.html',0,''),(22,'PikaChu','pikachu','10-2017/img (2)-2213925471329387.jpg',1,NULL,'PIKACHU',NULL,NULL,NULL,NULL,'2017-11-15 02:04:00','Game PIKACHU OFFLINE','deleted',NULL,'http://kiemthe.zing.vn/cam-nang/cai-dat-ngay/cai-dat-ngay.html','http://kiemthe.zing.vn/cam-nang/cai-dat-ngay/cai-dat-ngay.html',0,''),(23,'Li&ecirc;n Minh Huyền Thoại','lien-minh-huyen-thoai','10-2017/leagueoflegendsbig_5f2028ee86d150ad8c456113ec9c54e3-10592648139057.jpg',1,' Riot Games','Riot Games, Tencent Holdings Ltd, Garena',NULL,'Christian Linke',NULL,'Microsoft Windows, OS X','2009-10-27 00:00:00','Li&ecirc;n Minh Huyền Thoại (viết tắt: LMHT, tiếng Anh: League of Legends, viết tắt tiếng Anh: LoL) l&agrave; một tr&ograve; chơi video đấu trường trận chiến trực tuyến nhiều người chơi (MOBA - Multiplayer Online Battlefield Arena)  được Riot Games ph&aacute;t triển v&agrave; ph&aacute;t h&agrave;nh cho hệ điều h&agrave;nh Microsoft Windows[1] v&agrave; Mac OS X, lấy cảm hứng từ mod Defense of the Ancients[2] cho tr&ograve; chơi video Warcraft III: Frozen Throne.\r','active',NULL,'https://lienminh.garena.vn/','https://lienminh.garena.vn/download/',0,'&lt;div style=&quot;text-align: center;&quot;&gt;&lt;font color=&quot;#8b8b8b&quot; face=&quot;Roboto, sans-serif&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;OS: Windows 7 64-Bit / Windows 8 64-Bit / Windows 8.1 64-Bit&amp;nbsp; &lt;/span&gt;&lt;/font&gt;&lt;/div&gt;&lt;div style=&quot;text-align: center;&quot;&gt;&lt;font color=&quot;#8b8b8b&quot; face=&quot;Roboto, sans-serif&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;Processor: Intel&reg; Core&trade; i3-530 @ 2.93 GHz / AMD Phenom&trade; II X4 810 @ 2.60 GHz &lt;/span&gt;&lt;/font&gt;&lt;/div&gt;&lt;div style=&quot;text-align: center;&quot;&gt;&lt;font color=&quot;#8b8b8b&quot; face=&quot;Roboto, sans-serif&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;Memory: 6 GB RAM &lt;/span&gt;&lt;/font&gt;&lt;/div&gt;&lt;div style=&quot;text-align: center;&quot;&gt;&lt;font color=&quot;#8b8b8b&quot; face=&quot;Roboto, sans-serif&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;Graphics: NVIDIA&reg; GeForce&reg; GTX 470 @ 1GB / ATI&reg; Radeon&trade; HD 6970 @ 1GB DirectX: Version 11&lt;/span&gt;&lt;br&gt;&lt;/font&gt;&lt;/div&gt;'),(24,'Fifa Online 3','fifa-online-3','10-2017/19467600_1395283570538106_8475428301317264038_o-11154393062879.jpg',1,'Electronic Arts, Spearhead','Nexon , Garena',NULL,NULL,'FIFA 11 (2012－2015),  New Impact: FIFA 13 (2015－)','PC, Windows Android (FIFA Online 3 M)','2017-12-18 05:00:00','FIFA Online 3 l&agrave; một game thể thao do Electronic Arts ph&aacute;t triển v&agrave; ph&aacute;t h&agrave;nh v&agrave; ph&aacute;t h&agrave;nh v&agrave;o ng&agrave;y 18 th&aacute;ng 12 năm 2012 v&agrave; giai đoạn thứ nghiệm thứ nhất v&agrave;o ng&agrave;y 20 th&aacute;ng 9 năm 2012 đến ng&agrave;y 23 th&aacute;ng 9 năm đ&oacute; ở H&agrave;n Quốc. Tr&ograve; chơi n&agrave;y đang được ph&aacute;t h&agrave;nh ở một số quốc gia như Th&aacute;i Lan, Indonesia, Việt Nam, Singapore, Malaysia v&agrave; Trung Quốc. \r\n- Cấu h&igrave;nh tối thiểu:\r\nHệ điều h&agrave;nh: Window XP/Vista/7/8\r\nCPU: Core 2 Duo E4300 1.8 Ghz hoặc Athlon 64 X2 Dual Core 4600+\r\nRAM: 1GB (2 Gb nếu sử dụng win Vista/7/8)\r\nVGA: NVIDIA GeForce 6800 hay ATI Radeon X1600\r\nDirectX: DirectX 9.0c\r\n\r\n- Cấu h&igrave;nh đề nghị:\r\nHệ điều h&agrave;nh: Windows XP/Vista/7/8\r\n\r\nCPU : Core 2 Duo E6600 2.4 Ghz\r\nRAM : 2Gb\r\nCard đồ họa VGA : GeForce 8800 GT\r\nDirectX : DirectX 9.0c','active',NULL,'http://fo3.garena.vn/','http://fo3.garena.vn/download/game',0,'&lt;p style=&quot;margin-right: 0px; margin-bottom: 20px; margin-left: 0px; padding: 0px; border: 0px;